CREATE TABLE lude_backtest_results (
    id INT AUTO_INCREMENT PRIMARY KEY,
    correlation_type ENUM('正相关', '负相关') NOT NULL,
    factor VARCHAR(50) NOT NULL,
    strategy_combination VARCHAR(50) COMMENT '策略组合',
    cumulative_assets DECIMAL(15, 3) COMMENT '累计资产',
    total_return DECIMAL(15, 6) COMMENT '总收益率',
    annualized_return DECIMAL(15, 6) COMMENT '年化收益率',
    max_drawdown DECIMAL(10, 4) COMMENT '最大回撤率（百分比）',
    sharpe_ratio DECIMAL(10, 3) COMMENT '夏普比率',
    sortino_ratio DECIMAL(10, 3) COMMENT '索提诺比率',
    calmar_ratio DECIMAL(10, 3) COMMENT '卡玛比率',
    daily_turnover DECIMAL(15, 6) COMMENT '日均换手率',
    trading_cycles INT COMMENT '交易周期数',
    profitable_cycles INT COMMENT '盈利周期数',
    loss_cycles INT COMMENT '亏损周期数',
    win_rate DECIMAL(10, 6) COMMENT '胜率',
    profit_loss_ratio DECIMAL(15, 6) COMMENT '盈亏比',
    average_cycle_return DECIMAL(15, 6) COMMENT '平均每周期收益',
    max_single_cycle_profit DECIMAL(15, 6) COMMENT '最大单周期盈利',
    max_single_cycle_loss DECIMAL(15, 6) COMMENT '最大单周期亏损'
);
